clear allpkg load signal% gaussian smoothing filterfunction [out] = gaus(in)  sigma = 6;  mask_size = double(6.0 * sigma) + 1; % mask size  rad = floor(mask_size / 2);   [x] = meshgrid(-rad:rad);    gaussian = (1/(sqrt(2*pi)*(sigma)))*exp(-1 * (x.^2)./ (2*sigma.^2) );  gaussian = gaussian / sum(gaussian(:));    in = double(in);  out = conv2(in, gaussian, 'same');end% load original datasig = load('ppt.mat');sig = sig.x;% remove noise with gaussian smoothing filterf_sig = gaus(sig);% count peakspeak = findpeaks(f_sig);% Pulse per minute (sampling frequency = 100Hz)peaklength = length(peak);pulse_min = floor(peaklength/(19063/100/60)) subplot(1,3,1);plot(sig);subplot(1,3,2);plot(f_sig);subplot(1,3,3);plot(peak);